macro.dbt_synapse.materialization_test_s.macro_sql:
{%- materialization test, adapter='synapse' -%}

  {% set relations = [] %}

  {% set identifier = model['alias'] %}
  {% set old_relation = adapter.get_relation(database=database, schema=schema, identifier=identifier) %}
  {% set target_relation = api.Relation.create(
      identifier=identifier, schema=schema, database=database, type='table') -%} %}


  {% if old_relation %}
    {% do adapter.drop_relation(old_relation) %}
  {% elif not old_relation %}
    {% do adapter.create_schema(target_relation) %}
  {% endif %}

  {% call statement(auto_begin=True) %}
      {{ create_table_as(False, target_relation, sql) }}
  {% endcall %}

  {% set main_sql %}
      select *
      from {{ target_relation }}
  {% endset %}

  {{ adapter.commit() }}


  {% set limit = config.get('limit') %}
  {% set fail_calc = config.get('fail_calc') %}
  {% set warn_if = config.get('warn_if') %}
  {% set error_if = config.get('error_if') %}

  {% call statement('main', fetch_result=True) -%}

    {{ get_test_sql(main_sql, fail_calc, warn_if, error_if, limit)}}

  {%- endcall %}

  {% if should_store_failures() %}
    {% do relations.append(target_relation) %}
  {% elif not should_store_failures() %}
    {% do adapter.drop_relation(target_relation) %}
  {% endif %}

  {{ return({'relations': relations}) }}

{%- endmaterialization -%}

macro.dbt_synapse.synapse__basic_load_cs.macro_sql:
{% macro synapse__basic_load_csv_rows(model, batch_size, agate_table) %}

    {# Synapse does not support the TSQL's normal Table Value Constructor of #}
    {# INSERT INTO Dest_Table (cols) SELECT cols FROM Ref_Table #}

    {% set cols_sql = get_seed_column_quoted_csv(model, agate_table.column_names) %}
    {% set bindings = [] %}

    {% set statements = [] %}

    {% for chunk in agate_table.rows | batch(batch_size) %}
        {% set bindings = [] %}

        {% for row in chunk %}
            {% do bindings.extend(row) %}
        {% endfor %}

        {% set sql %}
            insert into {{ this.render() }} ({{ cols_sql }})
            {% for row in chunk -%}
                {{'SELECT'+' '}}
                {%- for column in agate_table.column_names -%}
                    {# TSQL catch 22: #}
                        {# strings must be single-quoted & #}
                        {# single-quotes inside of strings must be doubled #}

                    {% set col_type = agate_table.columns[column].data_type | string %}
                    {%- if "text.Text" in col_type -%}
                      '{{str_replace(row[column]) if row[column]}}'
                    {% else %}
                      '{{ row[column] if row[column] }}'
                    {%- endif -%}
                    {%- if not loop.last%}, {%- endif -%}
                {%- endfor -%}
                {%- if not loop.last-%} {{' '+'UNION ALL'+'\n'}} {%- endif -%}
            {%- endfor -%}
        {% endset %}

        {% do adapter.add_query(sql, abridge_sql_log=True) %}

        {% if loop.index0 == 0 %}
            {% do statements.append(sql) %}
        {% endif %}
    {% endfor %}

    {# Return SQL so we can render it out into the compiled files #}
    {{ return(statements[0]) }}
{% endmacro %}

macro.dbt_synapse.synapse__snapshot_merg.macro_sql:
{% macro synapse__snapshot_merge_sql(target, source, insert_cols) -%}
      {%- set insert_cols_csv = insert_cols | join(', ') -%}

      EXEC('
           BEGIN TRANSACTION
           update {{ target }}
          set dbt_valid_to = TMP.dbt_valid_to
          from {{ source }} TMP
          where {{ target }}.dbt_scd_id = TMP.dbt_scd_id
            and TMP.dbt_change_type in (''update'', ''delete'')
            and {{ target }}.dbt_valid_to is null;

            insert into {{ target }} (
                  {{ insert_cols_csv }}
                  )
            select {{ insert_cols_csv }}
            from {{ source }}
            where dbt_change_type = ''insert'' ;
           COMMIT TRANSACTION;
           ');

{% endmacro %}

